import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes, faClock } from '@fortawesome/free-solid-svg-icons';

const SettingsMenu = ({ isVisible, onClose, pomodoroTime, setPomodoroTime, shortBreakTime, setShortBreakTime, longBreakTime, setLongBreakTime }) => {
  if (!isVisible) return null;

  const handleSettingChange = (setter) => (e) => {
    setter(e.target.value);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-30">
      <div className="bg-white rounded-lg shadow-lg w-96 p-6 relative">
        <button onClick={onClose} className="absolute top-2 right-2 text-gray-600 hover:text-gray-900">
          <FontAwesomeIcon icon={faTimes} />
        </button>
        <h3 className="text-xl font-bold mb-4 flex items-center">
          <FontAwesomeIcon icon={faClock} className="mr-2" /> Timer
        </h3>
        <div className="mb-4">
          <label className="block mb-2 font-semibold">Pomodoro Time:</label>
          <input
            type="number"
            value={pomodoroTime}
            onChange={handleSettingChange(setPomodoroTime)}
            className="block w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2 font-semibold">Short Break Time:</label>
          <input
            type="number"
            value={shortBreakTime}
            onChange={handleSettingChange(setShortBreakTime)}
            className="block w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2 font-semibold">Long Break Time:</label>
          <input
            type="number"
            value={longBreakTime}
            onChange={handleSettingChange(setLongBreakTime)}
            className="block w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2 font-semibold">Long Break Interval:</label>
          <input
            type="number"
            value={4} // Default value, you can change it as needed
            onChange={handleSettingChange(setLongBreakTime)}
            className="block w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4 flex items-center">
          <label className="mr-2 font-semibold">Auto Start Breaks:</label>
          <input type="checkbox" className="transform scale-125" />
        </div>
        <div className="mb-4 flex items-center">
          <label className="mr-2 font-semibold">Auto Start Pomodoros:</label>
          <input type="checkbox" className="transform scale-125" />
        </div>
      </div>
    </div>
  );
};

export default SettingsMenu;
