import React, { useState, useEffect, useRef } from 'react';
import './index.css';
import Logo from './Picture/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePollVertical, faCog, faSignInAlt, faBars } from '@fortawesome/free-solid-svg-icons';
import SettingsMenu from './components/SettingsMenu';

function App() {
  const [menuVisible, setMenuVisible] = useState(false);
  const [selectedButton, setSelectedButton] = useState('pomodoro');
  const [settingsVisible, setSettingsVisible] = useState(false);
  const [pomodoroTime, setPomodoroTime] = useState(25);
  const [shortBreakTime, setShortBreakTime] = useState(5);
  const [longBreakTime, setLongBreakTime] = useState(15);
  const [autoStartBreaks, setAutoStartBreaks] = useState(false);
  const [autoStartPomodoros, setAutoStartPomodoros] = useState(false);
  const [timeLeft, setTimeLeft] = useState(pomodoroTime * 60);
  const [isRunning, setIsRunning] = useState(false);
  const intervalRef = useRef(null);

  useEffect(() => {
    setTimeLeft(pomodoroTime * 60);
  }, [pomodoroTime]);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(intervalRef.current);
            setIsRunning(false);
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
    } else {
      clearInterval(intervalRef.current);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const handleButtonClick = (buttonName) => {
    setSelectedButton(buttonName);
    if (buttonName === 'pomodoro') setTimeLeft(pomodoroTime * 60);
    if (buttonName === 'shortBreak') setTimeLeft(shortBreakTime * 60);
    if (buttonName === 'longBreak') setTimeLeft(longBreakTime * 60);
    setIsRunning(false);
  };

  const toggleSettings = () => {
    setSettingsVisible(!settingsVisible);
  };

  const handleStart = () => {
    setIsRunning(true);
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  return (
    <div className="bg-blue-50 min-h-screen overflow-hidden">
      <header className="flex justify-between items-center p-4 bg-white shadow-md border-b-2 border-blue-300 px-10">
        <div className="flex items-center space-x-4">
          <a href="/" className="flex items-center space-x-2">
            <img src={Logo} alt="Pomofocus Logo" className="w-12 h-12 rounded-full" />
            <span className="text-blue-600 text-xl font-bold">TP POMODORO</span>
          </a>
        </div>
        <nav>
          <ul className="flex space-x-4">
            <li>
              <button className="bg-blue-500 text-white px-4 py-2 rounded flex items-center shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform">
                <FontAwesomeIcon icon={faSquarePollVertical} className="mr-2" />
                Report
              </button>
            </li>
            <li>
              <button onClick={toggleSettings} className="bg-blue-500 text-white px-4 py-2 rounded flex items-center shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform">
                <FontAwesomeIcon icon={faCog} className="mr-2" />
                Setting
              </button>
            </li>
            <li>
              <button className="bg-blue-500 text-white px-4 py-2 rounded flex items-center shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform">
                <FontAwesomeIcon icon={faSignInAlt} className="mr-2" />
                Sign In
              </button>
            </li>
            <li className="relative">
              <button onClick={toggleMenu} className="bg-blue-500 text-white px-4 py-2 rounded shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform focus:outline-none">
                <FontAwesomeIcon icon={faBars} />
              </button>
              {menuVisible && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-20">
                  <button className="block text-left w-full px-4 py-2 text-gray-800 hover:bg-blue-500 hover:text-white transition duration-150 ease-in-out">Shortcuts</button>
                </div>
              )}
            </li>
          </ul>
        </nav>
      </header>

      <main className="flex flex-col items-center justify-center h-screen bg-blue-100">
        <div className="bg-blue-200 p-10 rounded-xl shadow-lg text-center">
          <div className="flex justify-center space-x-4 mb-6">
            <button onClick={() => handleButtonClick('pomodoro')} className={`timer-btn ${selectedButton === 'pomodoro' ? 'bg-blue-800' : 'bg-blue-500'} text-white px-4 py-2 rounded-full shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform`}>Pomodoro</button>
            <button onClick={() => handleButtonClick('shortBreak')} className={`timer-btn ${selectedButton === 'shortBreak' ? 'bg-blue-800' : 'bg-blue-500'} text-white px-4 py-2 rounded-full shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform`}>Short Break</button>
            <button onClick={() => handleButtonClick('longBreak')} className={`timer-btn ${selectedButton === 'longBreak' ? 'bg-blue-800' : 'bg-blue-500'} text-white px-4 py-2 rounded-full shadow-md hover:bg-blue-600 transform hover:scale-105 transition-transform`}>Long Break</button>
          </div>
          <div className="text-blue-900 text-6xl mb-6">
            {formatTime(timeLeft)}
          </div>
          <button onClick={handleStart} className="bg-white text-blue-700 px-6 py-3 rounded-full shadow-md hover:bg-blue-100 transform hover:scale-105 transition-transform">START</button>
        </div>
      </main>

      <SettingsMenu
        isVisible={settingsVisible}
        onClose={toggleSettings}
        pomodoroTime={pomodoroTime}
        setPomodoroTime={setPomodoroTime}
        shortBreakTime={shortBreakTime}
        setShortBreakTime={setShortBreakTime}
        longBreakTime={longBreakTime}
        setLongBreakTime={setLongBreakTime}
        autoStartBreaks={autoStartBreaks}
        setAutoStartBreaks={setAutoStartBreaks}
        autoStartPomodoros={autoStartPomodoros}
        setAutoStartPomodoros={setAutoStartPomodoros}
      />
    </div>
  );
}

export default App;
